import React, { useEffect, useState, useRef } from 'react';
import Navbar from "../components/Navbar.jsx";
import maplibregl from 'maplibre-gl'; // Import MapLibre
import 'maplibre-gl/dist/mapbox-gl.css'; // Import styles
import styles from "../styles/Map.module.css";

function Map() {
  const mapRef = useRef();
  const mapContainerRef = useRef();
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    mapRef.current = new maplibregl.Map({
      container: mapContainerRef.current,
      style: 'https://demotiles.maplibre.org/styles/basic/style.json', // Map style from MapLibre
      center: [-71.1685, 42.3350], // Center on Boston College
      zoom: 16, // Zoom level for closer view
    });

    return () => {
      mapRef.current.remove();
    };
  }, []);

  const handleSearch = async () => {
    if (searchQuery) {
      const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${searchQuery}`);
      const data = await response.json();
      if (data[0]) {
        const { lat, lon } = data[0];
        mapRef.current.flyTo({
          center: [lon, lat],
          zoom: 16,
        });
      }
    }
  };

  return (
    <main>
      <Navbar />
      <div className={styles['search-bar']}>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search for a location"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div ref={mapContainerRef} className={styles['map-container']} />
    </main>
  );
}

export default Map;
