import React, { useRef, useEffect, useState } from 'react';
import Navbar from "../components/Navbar.jsx"; 
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import styles from "../styles/Map.module.css";

function Map() {
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);

  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    // Replace with your actual MapTiler API key
    const apiKey = 'zxI0KxtpdcNfhTmR7PEA';

    // Initialize the map with high-quality detailed tiles from OpenStreetMap (OSM)
    mapRef.current = new maplibregl.Map({
      container: mapContainerRef.current, // HTML container reference
      style: `https://api.maptiler.com/maps/basic-v2/style.json?key=${apiKey}`, // Correct style URL format
      center: [-71.2057, 42.33506], // Boston College coordinates
      zoom: 15, // Adjust zoom for better clarity
      pitch: 45, // Adds a slight angle for a more 3D look (optional)
      bearing: 0, // Controls map rotation (set to 0 for north at the top)
    });

    // Cleanup the map when the component unmounts
    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
      }
    };
  }, []);

  const handleSearch = () => {
    if (searchQuery.trim()) {
      // Replace with your actual MapTiler API key for geocoding
      const apiKey = 'zxI0KxtpdcNfhTmR7PEA';
      fetch(`https://api.maptiler.com/geocoding/${searchQuery}.json?key=${apiKey}`)
        .then(response => response.json())
        .then(data => {
          if (data.features && data.features.length > 0) {
            const location = data.features[0].geometry.coordinates;
            mapRef.current.flyTo({
              center: location,
              zoom: 14,
            });
          } else {
            alert("Location not found");
          }
        })
        .catch(err => {
          console.error("Geocoding error:", err);
          alert("An error occurred while searching for the location.");
        });
    }
  };

  return (
    <main>
      <Navbar />
      <div className={styles['search-container']}>
        <div className={styles['events-label']}>
          Events near: 
        </div>
        <div className={styles['search-bar']}>
          <input
            type="text"
            placeholder="Search location"
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
      </div>
      <div ref={mapContainerRef} className={styles['map-container']} />
    </main>
  );
}

export default Map;
