import React, { useRef, useEffect } from 'react';
import * as maptilersdk from '@maptiler/sdk';
import '@maptiler/sdk/dist/maptiler-sdk.css';
import '../styles/Map.module.css';

export default function Map() {
  const mapContainer = useRef(null);
  const map = useRef(null);

  // Set the coordinates for Boston College
  const bostonCollege = { lng: -71.20509, lat: 42.33506 };
  const zoom = 14;  // Set a good zoom level

  // Set the API Key
  maptilersdk.config.apiKey = 'YOUR_MAPTILER_API_KEY';  // Replace with your valid MapTiler API key

  useEffect(() => {
    // Prevent multiple initializations
    if (map.current) return;

    // Initialize MapTiler SDK Map centered around Boston College
    map.current = new maptilersdk.Map({
      container: mapContainer.current,
      style: maptilersdk.MapStyle.STREETS,  // Use one of MapTiler's pre-built styles
      center: [bostonCollege.lng, bostonCollege.lat],
      zoom: zoom,
    });

    // Cleanup on unmount
    return () => {
      if (map.current) {
        map.current.remove();
      }
    };
  }, [bostonCollege.lng, bostonCollege.lat, zoom]);

  return (
    <div className="map-wrap">
      <div ref={mapContainer} className="map" style={{ width: '100%', height: '100%' }} />
    </div>
  );
}
