import React, { useRef, useEffect, useState } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import Navbar from '../components/Navbar';
import styles from '../styles/Map.module.css';

const hardcodedPosts = [
    {
      title: 'Sustainable Crafts Fair',
      tags: ['on-campus', 'events'],
      coords: [-71.171, 42.3355],
      colorClass: 'postColor1', // green
    },
    {
      title: 'Walsh Hall Closet Swap',
      tags: ['thrift', 'events'],
      coords: [-71.165, 42.3365],
      colorClass: 'postColor2', // blue
    }
  ];

export default function MapWithForum() {
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const BC = [-71.165, 42.3355]; 

  useEffect(() => {
    const map = new maplibregl.Map({
      container: mapContainerRef.current,
      style: 'https://api.maptiler.com/maps/basic-v2/style.json?key=zxI0KxtpdcNfhTmR7PEA',
      center: BC,
      zoom: 14,
    });

    mapRef.current = map;

    map.on('load', () => {
      hardcodedPosts.forEach((post) => {
        // Define icon SVGs
        const iconMap = {
          postColor1: {
            svg: `<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#27ae60" viewBox="0 0 24 24"><path d="M12 0l3 9h-6l3-9zm9 9h-6l3 9 3-9zm-18 0l3 9 3-9h-6zm3 11h12v4h-12v-4z"/></svg>`, // paintbrush style
          },
          postColor2: {
            svg: `<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#2980b9" viewBox="0 0 24 24"><path d="M18 3h-12v4l6 3 6-3v-4zm-12 6v12h12v-12l-6 3-6-3z"/></svg>` // shirt style
          }
        };

        const markerEl = document.createElement('div');
        markerEl.className = 'custom-marker';
        markerEl.innerHTML = iconMap[post.colorClass].svg;

        new maplibregl.Marker(markerEl)
          .setLngLat(post.coords)
          .setPopup(new maplibregl.Popup().setHTML(`<h3>${post.title}</h3><p>Event at Boston College</p>`))
          .addTo(map);
      });

      setMapLoaded(true);
    });

    return () => {
      map.remove();
    };
  }, []);



  const handlePostClick = (post) => {
    if (!mapRef.current || !mapLoaded) return;
    mapRef.current.flyTo({ center: post.coords, zoom: 15 });
  };

  const handleSearch = () => {
    if (searchQuery.trim() === '') return;


    const apiKey = 'zxI0KxtpdcNfhTmR7PEA';
    fetch(`https://api.maptiler.com/geocoding/${searchQuery}.json?key=${apiKey}`)
      .then(response => response.json())
      .then(data => {
        if (data.features && data.features.length > 0) {
          const location = data.features[0].geometry.coordinates;
          if (mapRef.current) {
            mapRef.current.flyTo({
              center: location,
              zoom: 14,
            });
          }
        } else {
          alert('Location not found');
        }
      })
  };

  return (
    <div className={styles.pageContainer}>
      <Navbar />

      <div className={styles.mainContent}>
        <div className={styles.leftPanel}>
          <div className={styles.searchBar}>
            <span className={styles.searchLabel}>Events near:</span>
            <input
              type="text"
              placeholder="Search location..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
          <div ref={mapContainerRef} className={styles.map}></div>
        </div>

        <div className={styles.rightPanel}>
          <div className={styles.tagFilters}>
            <span>Filter by tag:</span>
            <span className={styles.tag}>events</span>
            <span className={styles.tag}>on-campus</span>
            <span className={styles.tag}>thrift</span>
          </div>
          <hr />
          {hardcodedPosts.map((post, i) => (
  <div key={i} className={`${styles.postCard} ${styles[post.colorClass]}`} onClick={() => handlePostClick(post)}>
    <div className={styles.colorBar}></div>
    <div className={styles.postTitle}>{post.title}</div>
    <div className={styles.postTags}>
      {post.tags.map((tag, j) => (
        <span key={j} className={styles.tag}>{tag}</span>
      ))}
    </div>
  </div>
))}
        </div>
      </div>
    </div>
  );
}
