import React, { useRef, useEffect } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import '../styles/Map.module.css';

export default function Map() {
  const mapContainer = useRef(null);
  const map = useRef(null);

  const bostonCollegeCoords = { lng: -71.2057, lat: 42.33506 };
  const zoom = 14;

  useEffect(() => {
    if (map.current) return; // Stops the map from initializing more than once

    map.current = new maplibregl.Map({
      container: mapContainer.current,
      style: `https://api.maptiler.com/maps/basic-v2/style.json?key=ZufIDItILVQkUb143XDJ`, // Use MapTiler style URL
      center: [bostonCollegeCoords.lng, bostonCollegeCoords.lat],
      zoom: zoom,
    });

  }, [bostonCollegeCoords.lng, bostonCollegeCoords.lat, zoom]);

  return (
    <div className="map-wrap">
      <div ref={mapContainer} className="map" />
    </div>
  );
}
