import React, { useRef, useEffect } from 'react';
import Navbar from "../components/Navbar.jsx"; 
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import styles from "../styles/Map.module.css";

function Map() {
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);

  useEffect(() => {
    // Initialize the map with MapLibre settings
    mapRef.current = new maplibregl.Map({
      container: mapContainerRef.current, // HTML container reference
      style: 'https://demotiles.maplibre.org/style.json', // Use MapLibre style
      center: [-71.2057, 42.3601], // Coordinates for Boston College
      zoom: 14 // Adjust zoom level to get a good view of the area
    });

    // Cleanup the map when component unmounts
    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
      }
    };
  }, []);

  return (
    <main>
      <Navbar />
      <div id="map-container" ref={mapContainerRef} className={styles['map-container']} />
    </main>
  );
}

export default Map;
