import React, { useEffect, useRef } from 'react';
import Navbar from "../components/Navbar.jsx";
import maplibregl from 'maplibre-gl'; // Import maplibre
import 'maplibre-gl/dist/maplibre-gl.css'; // Import styles
import '@mapbox/mapbox-gl-geocoder/dist/mapbox-gl-geocoder.css'; // Geocoder styles
import MapboxGeocoder from '@mapbox/mapbox-gl-geocoder'; // Mapbox Geocoder for searching
import styles from "../styles/Map.module.css";

function Map() {
  const mapRef = useRef();
  const mapContainerRef = useRef();

  useEffect(() => {
    // Create the map
    mapRef.current = new maplibregl.Map({
      container: mapContainerRef.current,
      style: 'https://demotiles.maplibre.org/styles/basic/style.json', // Map style from MapLibre
      center: [-71.1685, 42.3350], // Center on Boston College
      zoom: 16, // Zoom level for closer view
    });

    // Add search control to the map
    const geocoder = new MapboxGeocoder({
      accessToken: 'YOUR_MAPBOX_ACCESS_TOKEN', // Replace with your Mapbox access token
      mapboxgl: maplibregl, // Connect MapLibre with Geocoder
    });

    mapRef.current.addControl(geocoder);

    geocoder.on('result', (e) => {
      const { center } = e.result.geometry;
      mapRef.current.flyTo({
        center, 
        essential: true, // Smooth fly to the location
        zoom: 16, // Zoom into the new location
      });
    });

    return () => {
      mapRef.current.remove();
    };
  }, []);

  return (
    <main>
      <Navbar />
      <div ref={mapContainerRef} className={styles['map-container']} />
    </main>
  );
}

export default Map;
